		glVertex3f(FaceArray[i].Vertex[0], FaceArray[i].Vertex[1], FaceArray[i].Vertex[2]);		glEnd();		//glDrawArrays(GL_TRIANGLES, 0, )		glBegin(GL_TRIANGLES);		for(i=0;i<pMesh->m_iNumberOfFaces;i++)		{			unsigned int j;			ObjFace *pf = &pMesh->m_aFaces[i];			for(j=0;j<3;j++)			{				glTexCoord2f( pMesh->m_aTexCoordArray[ pf->m_aTexCoordIndicies[j] ].u,					pMesh->m_aTexCoordArray[ pf->m_aTexCoordIndicies[j] ].v);				glNormal3f( pMesh->m_aNormalArray[ pf->m_aNormalIndices[j] ].x,					pMesh->m_aNormalArray[ pf->m_aNormalIndices[j] ].y,					pMesh->m_aNormalArray[ pf->m_aNormalIndices[j] ].z);				glVertex3f( pMesh->m_aVertexArray[ pf->m_aVertexIndices[j] ].x,					pMesh->m_aVertexArray[ pf->m_aVertexIndices[j] ].y,					pMesh->m_aVertexArray[ pf->m_aVertexIndices[j] ].z);			}		}